import React,{useState} from 'react';
import Filter from './Filter';
import Cardlist from './Cardlist';
import {jobs} from './data';

const App = () => {

    // for filter
    const [arrayInit,arrayNew] = useState([]);

    // for job list 
    const [job,setJobs] = useState(jobs);

    //when a skill is clicked
    const skillClick = (event) => {
        let name = event.target.getAttribute('name')

        if(arrayInit.includes(name) !== true){
            arrayNew(preValue=>{
            return [...preValue, name]  
            })
        }

        let filteredList = job.filter(position => {
            return arrayInit.every(i => job.skills.includes(i))
        })

        console.log(filteredList);
    }

    // delete filter bar 
    const clearClick = () => {
        arrayNew([]);
    }

    return(
        <div>
       <div className='image'></div>
       <Filter filtered={arrayInit}  clear={clearClick}/>
       <Cardlist click={skillClick} list={job}/>
        </div>
    )
}

export default App;
