import React from 'react';

const Card = ({image,company,date,position,details,skills}) => {
    if(date.includes('FEATURED')){
        document.querySelector('.Card').classList.add('leftBar');
    }

    return(
        <div className='Card'>
            <div className='leftCard'>
            {/* left image */}
           <img src={image} alt=''></img>

           {/* next to image */}
           <div className='imageNext'>
                <div className='date'>
                        <h4>{company}</h4>
                        {
                        date.map((recent,i)=>{
                        return <div key={i} className={i}>{recent}</div>
                        })
                        }
                </div>
                {/* position type */}
                    <h4>{position}</h4>
                    {/* date posted and location */}
                    <div className='details'> 
                        {
                        details.map((detail,i)=>{
                        return <div key={i} className={i}>{detail}</div>
                        })
                        }
                    </div>
           </div>
            </div>

            {/* skills */}
            <div className='skills'>
            {
                skills.map((skill,i)=>{
                    return <div className={i} key={i}>{skill}</div>
                })
            }
            </div>
        </div>
    )
}

export default Card;
