    }

    // delete filter bar 
    const clearClick = () => {
        arrayNew([]);
    }

    return(
        <div>
       <div className='image'></div>
       <Filter filtered={arrayInit}  clear={clearClick}/>
       <Cardlist click={skillClick} filter={filterJobs} list={job}/>
        </div>
    )
}

export default App;
