import React,{useState} from 'react';
import Filter from './Filter';
import Cardlist from './Cardlist';
import {jobs} from './data';

const App = () => {

    // for filter
    const [arrayInit,arrayNew] = useState([]);

    // for job list 
    const [job,setJobs] = useState(jobs);


    const skillClick = (event) => {
        let name = event.target.getAttribute('name')

        if(arrayInit.includes(name) !== true){
            arrayNew(preValue=>{
            return [...preValue, name]  
            })
        }

        setJobs(prevValue => {
         return prevValue.filter(item => {
             arrayInit.every(match => {
                 item.skills.includes(match)
             })
         })
        })
    }

    const clearClick = () => {
        arrayNew([]);
    }

    return(
        <div>
       <div className='image'></div>
       <Filter filtered={arrayInit}  clear={clearClick}/>
       <Cardlist click={skillClick} list={job}/>
        </div>
    )
}

export default App;
